import React , {Component} from 'react'

class CustomerSummary extends Component {
    constructor (props) {
        super (props)
    }

    render () {
        const {custCount = this.props.customerSummaryArray.length} = this.state
        return(
            <div>
                <input type= 'button' value = 'Calculate Tip & Cuatomer'

                />
                <table border="3" width='100%'>
                    <tr>
                        <th> Total Customer </th>
                        <th> Tip </th>
                    </tr>
                    <tr>
                        <th> {this.state.custCount} </th>
                        <th> Tip total </th>
                    </tr>
                </table>
            </div>
        )
    }
}

export default CustomerSummary
