import React , {Component} from 'react'

class CustomerSummary extends Component {
    constructor (props) {
        super (props)
    }

    render () {
        return(
            <div>
                <input type= 'button' value = 'Calculate Tip & Cuatomer'/>
                <table>
                    <tr>
                        <th> Total Customer </th>
                        <th> Tip </th>
                    </tr>
                    <tr>
                        <th> Customer Summ </th>
                        <th> Tip total </th>
                    </tr>
                </table>
            </div>
        )
    }
}

export default CustomerSummary
