import React , {Component} from 'react'

class CustomerSummary extends Component {
    constructor (props) {
        super (props)
        this.state={
            totalTip: 0
        }
    }
    customerTotal = (event) => {this.props.customerSummaryArray.map((item)=>(totalTip += item.tip))}
    render () {
               
        return(
            <div>
                <input type= 'button' value = 'Calculate Tip & Cuatomer'
                 onClick={this.customerTotal} />
                <table border="3" width='100%'>
                    <tr>
                        <th> Total Customer </th>
                        <th> Tip </th>
                    </tr>
                    <tr>
                        <th> {this.props.customerSummaryArray.length} </th>
                        <th> {this.state.totalTip} </th>
                    </tr>
                </table>
            </div>
        )
    }
}

export default CustomerSummary
